import React, { Component } from 'react';
import HeaderAfterLogin from '../../../components/user/HeaderAfterLogin'
import { MDBContainer, MDBRow, MDBCol, MDBBreadcrumb, MDBBreadcrumbItem, MDBIcon, MDBBtn, MDBCollapse, MDBFormInline } from "mdbreact";
import ListProductCart from './ListProductCart';
import PaymentCart from './PaymentCart';
import shipper from '../../../assets/images/common/delivery.png';

class Cart extends Component {
    state = {
        collapseID: ''
    };

    toggleCollapse = collapseID => () => {
        this.setState(prevState => ({
            collapseID: prevState.collapseID !== collapseID ? collapseID : ''
        }));
    };
    render() {
        const { collapseID } = this.state;

        // Css cho icon phần thông tin thêm
        const icon1 = {
            marginLeft: "4px",
            marginRight: "15px",
        };
        const icon2 = {
            marginLeft: "2px",
            marginRight: "13px",
        };
        const icon3 = {
            marginLeft: "1px",
            marginRight: "12px",
        };
        return (
            <div>
                <header>
                    <HeaderAfterLogin />
                </header>
                <main>

                    <MDBContainer>
                        {/* Đường dẫn */}
                        <MDBBreadcrumb light color='orange lighten-2' className="mt-5 mb-4">
                            <MDBBreadcrumbItem appendIcon icon='angle-right'>Home</MDBBreadcrumbItem>
                            <MDBBreadcrumbItem appendIcon icon='angle-right'>
                                Library
                            </MDBBreadcrumbItem>
                            <MDBBreadcrumbItem appendIcon icon='angle-right' active>
                                Data
                            </MDBBreadcrumbItem>
                        </MDBBreadcrumb>

                        <section className="mb-4">
                            <div className="row">
                                <div className="col-lg-8">
                                    {/* Danh sách sản phẩm */}
                                    <div className="card wish-list mb-4">
                                        <ListProductCart />
                                    </div>
                                    {/* Thẻ thông tin thêm */}
                                    <div className="card wish-list mb-4 orange lighten-2">
                                        <div className="card-body">
                                            <MDBRow className="d-flex justify-content-between align-items-center">
                                                <MDBCol md="2" className="d-none d-lg-block">
                                                    <img src={shipper} className="img-fluid" alt="" />
                                                </MDBCol>
                                                <MDBCol md="10" style={{ color: "#fafafa" }}>
                                                    <MDBIcon icon="map-marker-alt" style={icon1} /><span>Khoảng cách: <strong>2.5km</strong></span>
                                                    <br />
                                                    <MDBIcon icon="clock" style={icon2} /><span>Dự kiên giao hàng: <strong>15:30 - 15:40 (30 phút)</strong></span>
                                                    <br />
                                                    <MDBIcon icon="credit-card" style={icon3} /><span>Phương thức thanh toán: <strong>Thanh toán khi nhận được hàng</strong></span>
                                                </MDBCol>
                                            </MDBRow>
                                        </div>
                                    </div>
                                </div>
                                <div className="col-lg-4">
                                    <div className="card mb-4">
                                        <div className="card-body">
                                            <h5 className="mb-4">Cập nhật thông tin</h5>
                                            <div className="d-flex justify-content-between align-items-center">
                                                <MDBBtn 
                                                    color=""
                                                    onClick={this.toggleCollapse('basicCollapse1')}
                                                    style={{ marginBottom: '1rem' }}
                                                >
                                                    <MDBIcon fab icon="location-arrow" className="pr-1" />Địa điểm
                                                </MDBBtn>
                                                <MDBBtn 
                                                    color=""
                                                    onClick={this.toggleCollapse('basicCollapse2')}
                                                    style={{ marginBottom: '1rem' }}
                                                >
                                                    <MDBIcon fab icon="facebook-f" className="pr-1" />Giảm giá
                                                </MDBBtn>
                                            </div>
                                            <MDBCollapse id='basicCollapse1' isOpen={collapseID}>
                                                <MDBFormInline className="md-form mr-auto mb-4">
                                                    <input className="form-control mr-sm-2" type="text" placeholder="Search" aria-label="Search" />
                                                    <MDBBtn gradient="aqua" rounded size="sm" type="submit" className="mr-auto">
                                                        Search
                                                </MDBBtn>
                                                </MDBFormInline>
                                            </MDBCollapse>
                                            <MDBCollapse id='basicCollapse2' isOpen={collapseID}>
                                                <MDBFormInline className="md-form mr-auto mb-4">
                                                    <input className="form-control mr-sm-2" type="text" placeholder="Search" aria-label="Search" />
                                                    <MDBBtn gradient="aqua" rounded size="sm" type="submit" className="mr-auto">
                                                        Search
                                                </MDBBtn>
                                                </MDBFormInline>
                                            </MDBCollapse>
                                        </div>
                                    </div>

                                    <div className="card mb-4">
                                        <PaymentCart />
                                    </div>
                                </div>
                            </div>
                        </section>
                    </MDBContainer>
                </main>
            </div >
        );
    }
}

export default Cart;
