import React, { Component } from 'react';
import '../../../assets/fonts/user/Logout/material-icon/css/material-design-iconic-font.min.css';
import '../../../assets/css/user/Logout/style.css';
import img from'../../../assets/images/user/Logout/form-img.jpg';
import {main} from '../../../assets/js/user/Logout/main';

class Logout extends Component {
    componentDidMount(){
        main();
        onFocus = () => {

            this.setState({
              type: 'date'
            });
          };
          onBlur = () => {
        
            this.setState({
              type: 'text'
            });
          }
    };
    
    render() {
        return (
            <div className="main">
                <div className="container">
                    <div className="booking-content">
                        <div className="booking-image">
                            <img className="booking-img" src={img} alt="Booking Image" height="100%"/>
                        </div>
                        <div className="booking-form">
                            <form id="booking-form">
                                <h2>Đăng ký</h2>
                                <div className="form-group form-input">
                                    <input type="text" name="name" id="name"  required />
                                    <label htmlFor="name" className="form-label">Tên của bạn</label>
                                </div>
                                <div className="form-group form-input">
                                    <input type="text" name="name" id="name"  required />
                                    <label htmlFor="name" className="form-label">Địa chỉ</label>
                                </div>
                                <div className="form-group form-input">
                                    <input type="number" name="phone" id="phone"  required />
                                    <label htmlFor="phone" className="form-label">Số điện thoại</label>
                                </div>
                                <div className="form-group form-input">
                                    <input type="text" name="date" id="name"  required onFocus="(this.type='date')" onFocusout="(this.type='text')" />
                                    <label htmlFor="date" className="form-label">Ngày sinh</label>
                                </div>
                                <div className="form-group">
                                    <div className="select-list">
                                        <select name="gender" id="time" required>
                                            <option value>Giới tính</option>
                                            <option value={0}>Nữ</option>
                                            <option value={1}>Nam</option>
                                        </select>
                                    </div>
                                </div>
                                <div className="form-radio">
                                    {/* <label class="label-radio"> Select Your Dining Space</label> */}
                                    <div className="radio-item-list">
                                        <span className="radio-item">
                                            <input type="radio" name="number_people" defaultValue={2} id="number_people_2" />
                                            <label htmlFor="number_people_2">2</label>
                                        </span>
                                        <span className="radio-item active">
                                            <input type="radio" name="number_people" defaultValue={4} id="number_people_4" defaultChecked="checked" />
                                            <label htmlFor="number_people_4">4</label>
                                        </span>
                                        <span className="radio-item">
                                            <input type="radio" name="number_people" defaultValue={6} id="number_people_6" />
                                            <label htmlFor="number_people_6">6</label>
                                        </span>
                                        <span className="radio-item">
                                            <input type="radio" name="number_people" defaultValue={8} id="number_people_8" />
                                            <label htmlFor="number_people_8">8</label>
                                        </span>
                                        <span className="radio-item">
                                            <input type="radio" name="number_people" defaultValue={10} id="number_people_10" />
                                            <label htmlFor="number_people_10">10</label>
                                        </span>
                                    </div>
                                </div>
                                <div className="form-submit">
                                    <input type="submit" defaultValue="Book now" className="submit" id="submit" name="submit"/>
                                    <a href="#" className="checkout">Đã có tài khoản? Đăng nhập ngay!</a>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default Logout;
