// Import thư viện React
import React from 'react';
// Import các component 
import {
    MDBRow,
    MDBCarousel,
    MDBCarouselInner,
    MDBCarouselItem,
} from 'mdbreact';
import ItemSlideProduct from './ItemSlideProduct';

const SlideProduct = () => {
    return (
        <section className='text-center my-5'>
            <h2 className=' font-weight-bold text-center my-5'>Ưu đãi ngày hôm nay</h2>
            <MDBCarousel activeItem={1} length={3} slide={true} showControls={false} showIndicators={true} multiItem>
                <MDBCarouselInner className="px-1">
                    <MDBRow>
                        <MDBCarouselItem itemId='1'>
                            <ItemSlideProduct />
                            <ItemSlideProduct />
                            <ItemSlideProduct />
                        </MDBCarouselItem>
                        <MDBCarouselItem itemId='2'>
                            <ItemSlideProduct />
                            <ItemSlideProduct />
                            <ItemSlideProduct />
                        </MDBCarouselItem>
                        <MDBCarouselItem itemId='3'>
                            <ItemSlideProduct />
                            <ItemSlideProduct />
                            <ItemSlideProduct />
                        </MDBCarouselItem>
                    </MDBRow>
                </MDBCarouselInner>
            </MDBCarousel>
        </section>
    );
};

export default SlideProduct;
