import React, { Component } from 'react';
import HeaderAfterLogin from '../../../components/user/HeaderAfterLogin'
import { MDBContainer } from "mdbreact";
import ListProductCart from './ListProductCart';
import PaymentCart from './PaymentCart';
import shipper from './';

class Cart extends Component {
    render() {
        // Css cho ô thông tin kèm
        const background = {
            backgroundColor: "#ffb74d",
            color:"#fff3e0"
        };
        return (
            <div>
                <header>
                    <HeaderAfterLogin />
                </header>
                <main>
                    <MDBContainer>
                        <section className="mt-5 mb-4">
                            <div className="row">
                                <div className="col-lg-8">
                                    <div className="card wish-list mb-4">
                                        <ListProductCart />
                                    </div>
                                    <div className="card wish-list mb-4" style={background}>
                                        <div className="card-body">
                                            <img src="" alt=""></img>
                                            <p class="mb-0">Khoảng cách: <strong>2.5km</strong></p>
                                            <p class="mb-0">Dự kiên giao hàng: <strong>15:30 - 15:40 (30 phút)</strong></p>
                                            <p class="mb-0">Phương thức thanh toán: <strong>Thanh toán khi nhận được hàng</strong></p>
                                        </div>
                                    </div>
                                </div>
                                <div className="col-lg-4">
                                    <div className="card mb-4">
                                        <PaymentCart />
                                    </div>
                                </div>
                            </div>
                        </section>
                    </MDBContainer>
                </main>
            </div>
        );
    }
}

export default Cart;
