import React, { Component } from "react";
import {
  MDBContainer,
  MDBTabPane,
  MDBTabContent,
  MDBNav,
  MDBNavItem,
  MDBNavLink,
  MDBRow,
  MDBCol,
  MDBBtn,
  MDBCardBody,
  MDBCardTitle,
  MDBCardText,
  MDBIcon,
} from "mdbreact";
import { BrowserRouter as Router } from "react-router-dom";

class TabInfo extends Component {
  state = {
    activeItemOuterTabs: "1",
    activeItemInnerPills: "1",
  };

  toggleOuterTabs = tab => e => {
    if (this.state.activeItemOuterTabs2 !== tab) {
      this.setState({
        activeItemOuterTabs: tab
      });
    }
  };

  toggleInnerPills = tab => e => {
    if (this.state.activeItemInnerPills !== tab) {
      this.setState({
        activeItemInnerPills: tab
      });
    }
  };

  render() {
    return (
      <Router>
        <MDBContainer className="my-5 pb-5 ">
          <MDBNav style={{ paddingLeft: "0.7rem" }} classicTabs className="nav-justified" >
            <MDBNavItem>
              <MDBNavLink link to="#" active={this.state.activeItemOuterTabs === "1"} onClick={this.toggleOuterTabs("1")} role="tab" >
                <MDBIcon icon="user" /> Mô tả
            </MDBNavLink>
            </MDBNavItem>
            <MDBNavItem>
              <MDBNavLink link to="#" active={this.state.activeItemOuterTabs === "2"} onClick={this.toggleOuterTabs("2")} role="tab" >
                <MDBIcon icon="heart" /> Thông tin shop
            </MDBNavLink>
            </MDBNavItem>
            <MDBNavItem>
              <MDBNavLink link to="#" active={this.state.activeItemOuterTabs === "3"} onClick={this.toggleOuterTabs("3")} role="tab" >
                <MDBIcon icon="heart" /> Đánh giá
            </MDBNavLink>
            </MDBNavItem>
          </MDBNav>
          <MDBTabContent
            className="mb-5"
            activeItem={this.state.activeItemOuterTabs}
          >
            <MDBTabPane tabId="1" role="tabpanel">
              
            </MDBTabPane>
            <MDBTabPane tabId="2" role="tabpanel">
              <MDBRow>
                <MDBCol md="6">
                  <MDBCardBody>
                    <MDBCardTitle>Special Title Treatment</MDBCardTitle>
                    <MDBCardText>
                      With supporting text below as a natural lead-in to
                      additional content.
                  </MDBCardText>
                    <MDBBtn>Go somewhere</MDBBtn>
                  </MDBCardBody>
                </MDBCol>
                <MDBCol md="6">
                  <MDBCardBody>
                    <MDBCardTitle>Special Title Treatment</MDBCardTitle>
                    <MDBCardText>
                      With supporting text below as a natural lead-in to
                      additional content.
                  </MDBCardText>
                    <MDBBtn>Go somewhere</MDBBtn>
                  </MDBCardBody>
                </MDBCol>
              </MDBRow>
            </MDBTabPane>
          </MDBTabContent>
        </MDBContainer>
      </Router>
    );
  }
}
export default TabInfo;
